import axios from "axios";
import React, { useState } from "react";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

export default function AdminEventComp() {
  const initialState = {
    event_name: "",
    event_description: "",
    event_image: "",
    event_link: "",
  };

  const [state, setState] = useState(initialState);
  const { event_name, event_description, event_link, event_image } = state;
  const navigate = useNavigate();
  const handelSubmit = (e) => {
    e.preventDefault();
    if (!event_name || !event_description || !event_link || !event_image) {
      toast.error("please fill all the fields");
    } else {
      axios
        .post("http://localhost:3001/api/post", {
          event_name,
          event_description,
          event_link,
          event_image,
        })
        .then(() => {
          setState({
            event_name: "",
            event_description: "",
            event_link: "",
            event_image: "",
          });
        })
        .catch((err) => {
          toast.error(err.response.data);
        });
      setTimeout(() => {
        toast.success("event added");
        navigate("/adminevents");
      }, 500);
    }
  };

  const handelChange = (e) => {
    const { name, value } = e.target;

    if (name === "event_image") {
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onloadend = () => {
        setState({ ...state, [name]: reader.result });
        console.log({ ...state });
      };
    } else {
      setState({ ...state, [name]: value });
    }
  };
  return (
    <div class="container">
      <div class="row justify-content-center">
        <div class="col-md-6">
          <h2>Create an Event</h2>
          <form enctype="multipart/form-data">
            <div class="form-group">
              <label for="eventName">Event Name:</label>
              <input
                type="text"
                class="form-control"
                id="eventName"
                placeholder="Enter event name"
                name="event_name"
                value={event_name}
                onChange={handelChange}
                required
              />
            </div>
            <div class="form-group">
              <label for="eventDescription">Event Description:</label>
              <textarea
                class="form-control"
                id="eventDescription"
                rows="3"
                placeholder="Enter event description"
                name="event_description"
                value={event_description}
                onChange={handelChange}
              ></textarea>
            </div>
            <div class="form-group">
              <label for="eventImage">Event Image:</label>
              <input
                type="file"
                class="form-control-file"
                id="eventImage"
                name="event_image"
                onChange={handelChange}
              />
            </div>
            <div class="form-group">
              <label for="eventLink">Event Link:</label>
              <input
                type="url"
                class="form-control"
                id="eventLink"
                placeholder="Enter event link"
                name="event_link"
                value={event_link}
                onChange={handelChange}
              />
            </div>
            <button
              type="submit"
              class="btn btn-primary"
              onClick={handelSubmit}
            >
              Create Event
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
