/* eslint-disable jsx-a11y/img-redundant-alt */
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";

export default function EventPageComp() {
  const [event, setevent] = useState([]);

  const loadEvent = async () => {
    try {
      const response = await axios.get("http://localhost:3001/api/getall");
      setevent(response.data);
    } catch (error) {
      console.error(error);
      toast.error("Error loading events.");
    }
  };

  useEffect(() => {
    loadEvent();
  }, []);
  return (
    <div>
      <div class="container-xxl">
        <div class="row mt-4">
          {event.map((item, index) => {
            return (
              <div class="col mt-5" key={index}>
                <div class="card">
                  <img
                    src={item.event_image}
                    class="img-fluid w-100 h-100 card-img-top"
                    height="200"
                    alt="..."
                  />

                  <div class="card-body">
                    <h5 class="card-title">{item.event_name}</h5>
                    <h6 class="card-title">{item.event_date}</h6>
                    {event.event_description}
                    &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                    <span class="ml-3">Edit</span>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
