import React, { useState } from "react";
import "./feedbackcomp.css";
import axios from "axios";

export default function FeedbackComp() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [phone, setPhone] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .post("/feedback", { name, email, message })
      .then((response) => {
        alert(response.data);
      })
      .catch((error) => {
        console.log(error);
        alert("Error submitting feedback");
      });
  };
  return (
    <>
      <section id="get-started" class="get-started section-bg">
        <div class="container-fluid p-4" id="container_for_feedback">
          <div class="row justify-content-center  gy-4">
            <div
              class="col-lg-5 p-4 rounded-4"
              data-aos="fade"
              id="container_for_the_form"
            >
              <form
                action="forms/quote.php"
                method="post"
                class="php-email-form"
              >
                <h3>Feedback</h3>
                <p>
                  Vel nobis odio laboriosam et hic voluptatem. Inventore vitae
                  totam. Rerum repellendus enim linead sero park flows.
                </p>
                <div class="row gy-3">
                  <div class="col-md-12">
                    <input
                      type="text"
                      name="name"
                      class="form-control"
                      placeholder="Name"
                      required
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                    />
                  </div>

                  <div class="col-md-12 ">
                    <input
                      type="email"
                      class="form-control"
                      name="email"
                      placeholder="Email"
                      required
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                    />
                  </div>

                  <div class="col-md-12">
                    <input
                      type="text"
                      class="form-control"
                      name="phone"
                      placeholder="Phone"
                      required
                    />
                  </div>

                  <div class="col-md-12">
                    <textarea
                      class="form-control"
                      name="message"
                      rows="6"
                      placeholder="Message"
                      required
                      value={message}
                      onChange={(e) => setMessage(e.target.value)}
                    ></textarea>
                  </div>

                  <div class="col-md-12 text-center">
                    <button type="submit" class="btn" id="sent_feedback_button">
                      Sent
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
