import axios from "axios";
import React, { useRef, useState } from "react";
import { toast } from "react-toastify";

export default function AdminEventComp() {
  const initialState = {
    event_name: "",
    event_description: "",
    event_link: "",
  };

  const [state, setState] = useState(initialState);
  const { event_name, event_description, event_link } = state;

  const handelSubmit = (e) => {
    e.preventDefault();
    if (!event_name || !event_description || !event_link) {
      toast.error("please fill all the fields");
    } else {
      axios
        .post("http://localhost:3001/api/post", {
          event_name,
          event_description,
          event_link,
        })
        .then(() => {
          setState({ event_name: "", event_description: "", event_link: "" });
        })
        .catch((err) => {
          toast.error(err.response.data);
        });
      setTimeout(() => {
        history.pushState("/");
      }, 500);
    }
  };

  const handelChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };
  return (
    <div class="container">
      <div class="row justify-content-center">
        <div class="col-md-6">
          <h2>Create an Event</h2>
          <form>
            <div class="form-group">
              <label for="eventName">Event Name:</label>
              <input
                type="text"
                class="form-control"
                id="eventName"
                placeholder="Enter event name"
                value={event_name}
                onChange={handelChange}
                required
              />
            </div>
            <div class="form-group">
              <label for="eventDescription">Event Description:</label>
              <textarea
                class="form-control"
                id="eventDescription"
                rows="3"
                placeholder="Enter event description"
                value={event_description}
                onChange={handelChange}
              ></textarea>
            </div>
            <div class="form-group">
              <label for="eventImage">Event Image:</label>
              <input type="file" class="form-control-file" id="eventImage" />
            </div>
            <div class="form-group">
              <label for="eventLink">Event Link:</label>
              <input
                type="url"
                class="form-control"
                id="eventLink"
                placeholder="Enter event link"
                value={event_link}
                onChange={handelChange}
              />
            </div>
            <button type="submit" class="btn btn-primary">
              Create Event
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
